import React, {FC, useContext} from "react";
// material-ui
import {
  Card,
  CardContent,
  Divider,
  Typography,
  Button,
  Collapse,
  Grid
} from "@material-ui/core";

interface TextCardProps {
    month: string,
    date: string,
    start: string,
    destination: string,
    time: string,
    capacity: string
}

const Item:FC<TextCardProps> = (props) => {
  const [expanded, setExpanded] = React.useState(false);
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <div>
        <Divider/>
        <div>
          <Button
            fullWidth
            onClick={handleExpandClick}
          >
            {expanded ? "閉じるぞ" : "開くぞ"}
          </Button>
        </div>
      <Card>
        <Divider/>
        <CardContent>
          <Collapse in={!expanded} timeout="auto" unmountOnExit/>
          <Collapse in={expanded} timeout="auto" unmountOnExit>
            <Typography variant="body1">
            <div>
                <Grid item container>
                    <Grid sm ={4}>
                        <Grid sm={6}>
                            日時：{props.month}/{props.date}
                        </Grid>
                        <Grid sm={6}>
                            日時：{props.month}/{props.date}
                        </Grid>
                        <Grid sm={6}>
                            日時：{props.month}/{props.date}
                        </Grid>          
                    </Grid>
                    <Grid sm={4}/>
                </Grid>
                出発場所：{props.start}　→　到着場所：{props.destination}<br/>
                出発時間：{props.time}<br/>
                定員：{props.capacity}
            </div>
            </Typography>
          </Collapse>
        </CardContent>
      </Card>
    </div>
  );
};

export default Item
